queries.aggrQuery:
{
  "aggs": {
    "AGGR": {
      "filter": {
        "bool": {
          "must_not": [
            {
              "term": {
                "Data.tenantId.keyword": "pb.testing"
              }
            }
          ]
        }
      },
      "aggs": {
        "ULBs": {
          "terms": {
            "field": "Data.tenantId.keyword",
            "size": 1000
          },
          "aggs": {
            "Total Applications Submitted": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA",
                          "BPA_LOW"
                        ]
                      }
                    }
                  ]
                }
              }
            },
            "Total Permits Issued": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    },
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA",
                          "BPA_LOW"
                        ]
                      }
                    }
                  ]
                }
              }
            },
            "Average days to issue Permit": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA",
                          "BPA_LOW"
                        ]
                      }
                    },
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    }
                  ]
                }
              },
              "aggs": {
                "average_days": {
                  "avg": {
                    "script": {
                      "source": "(doc['Data.auditDetails.lastModifiedTime'].value-doc['Data.auditDetails.createdTime'].value)/(86400*1000)"
                    }
                  }
                }
              }
            },
            "Average days to issue OC": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "term": {
                        "Data.businessService.keyword": "BPA_OC"
                      }
                    },
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    }
                  ]
                }
              },
              "aggs": {
                "average_days": {
                  "avg": {
                    "script": {
                      "source": "(doc['Data.auditDetails.lastModifiedTime'].value-doc['Data.auditDetails.createdTime'].value)/(86400*1000)"
                    }
                  }
                }
              }
            },
            "SLA Compliance Permit": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA",
                          "BPA_LOW"
                        ]
                      }
                    },
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    },
                    {
                      "script": {
                        "script": {
                          "source": "doc['Data.auditDetails.lastModifiedTime'].value-doc['Data.auditDetails.createdTime'].value<params.threshold",
                          "lang": "painless",
                          "params": {
                            "threshold": 172800000
                          }
                        }
                      }
                    }
                  ]
                }
              }
            },
            "SLA Compliance OC": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "term": {
                        "Data.businessService.keyword": "BPA_OC"
                      }
                    },
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    },
                    {
                      "script": {
                        "script": {
                          "source": "doc['Data.auditDetails.lastModifiedTime'].value-doc['Data.auditDetails.createdTime'].value<params.threshold",
                          "lang": "painless",
                          "params": {
                            "threshold": 172800000
                          }
                        }
                      }
                    }
                  ]
                }
              }
            },
            "Total OC Issued": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    },
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA_OC"
                        ]
                      }
                    }
                  ]
                }
              }
            },
            "Total OC Submitted": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA_OC"
                        ]
                      }
                    }
                  ]
                }
              }
            },
            "Deviation": {
              "filter": {
                "bool": {
                  "must": [
                    {
                      "terms": {
                        "Data.businessService.keyword": [
                          "BPA_OC",
                          "BPA",
                          "BPA_LOW"
                        ]
                      }
                    },
                    {
                      "term": {
                        "Data.status.keyword": "APPROVED"
                      }
                    },
                    {
                      "exists": {
                        "field": "Data.plotAreaApproved"
                      }
                    }
                  ]
                }
              },
              "aggs": {
                "deviation": {
                  "avg": {
                    "script": {
                      "source": " Math.round(((doc['Data.plotAreaApproved'].value-doc['Data.plotArea'].value)*100)/(doc['Data.plotArea'].value))"
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
