                  "sum": {
                    "field": "Data.physicalCount"
                  }
                }
              }
            },
            "Outgoing Stock": {
              "filter": {
                "term": {
                  "Data.eventType.keyword": "DISPATCHED"
                }
              },
              "aggs": {
                "SUM": {
                  "sum": {
                    "field": "Data.physicalCount"
                  }
                }
              }
            },
            "Total Stock": {
              "bucket_script": {
                "buckets_path": {
                  "IS": "Incoming Stock>SUM",
                  "OS": "Outgoing Stock>SUM"
                },
                "script": "params.IS-params.OS"
              }
            }
          }
        }
      }
    }
  }
}

